<template>
  <section>
    <div class="flex py-10">
      <div class="w-1/12 flex items-center ">
        <div  class="w-full p-4">
          <button @click="prev" class="text-right bg-blue-500 p-2 text-white text-sm rounded-full ">prev</button>
        </div>
      </div>
      <div class="w-10/12  overflow-hidden" ref="sliderContainer" id="sliderContainer">
        <ul id="slider" ref="slider" class="flex w-full transition-all duration-700 " >
          <li class="p-5">
            <div class="border rounded-lg p-5 h-full ">
              <img class="h-32 w-full  rounded-md  object-cover" src="data:image/jpeg;base64," alt="">
            </div>
          </li>
          <li class="p-5">
            <div class="border rounded-lg p-5 h-full ">
              <img class="h-32 w-full  rounded-md  object-cover" src="data:image/jpeg;base64," alt="">
            </div>
          </li>
           <li class="p-5">
            <div class="border rounded-lg p-5 h-full ">
              <img class="h-32 w-full  rounded-md  object-cover" src="data:image/jpeg;base64," alt="">
            </div>
          </li>
           <li class="p-5">
            <div class="border rounded-lg p-5 h-full ">
              <img class="h-32 w-full  rounded-md  object-cover" src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ0PujepGqpxXhx122po1Tiy8kuDTOh8NqvQw&usqp=CAU" alt="">
            </div>
          </li>
           <li class="p-5">
            <div class="border rounded-lg p-5 h-full ">
              <img class="h-32 w-full  rounded-md  object-cover" src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS71eg6AWFnZ5O_CpriahGY9x4sVDHnxR3l-w&usqp=CAU" alt="">
            </div>
          </li>
           <li class="p-5">
            <div class="border rounded-lg p-5 h-full ">
              <img class="h-32 w-full  rounded-md  object-cover" src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRxHi8gDHGF9fbQgAc0vnoul9CMCE8uibndRQ&usqp=CAU" alt="">
            </div>
          </li>

          
          
        </ul>
      </div>
      <div class="w-1/12 flex items-center ">
        <div class="w-full p-4">
          <button @click="next" class="text-left bg-blue-500 p-2 text-sm text-white rounded-full ">next</button>
        </div>
      </div>
    </div>
  </section>
</template>

<script>
  
export default {
  methods: {
    prev() {
      let slider = this.$refs.slider
      let cards = slider.getElementsByTagName('li')
      let sliderContainerWidth = this.$refs.sliderContainer.clientWidth
      let elementsToShow = 3
      let cardWidth = sliderContainerWidth / elementsToShow
      if(+slider.style.marginLeft.slice(0,-2) != -cardWidth * (cards.length - elementsToShow) ) {
        slider.style.marginLeft = ((+slider.style.marginLeft.slice(0,-2)) -cardWidth) + 'px'
      }
     
    },
    next() {
      let slider = this.$refs.slider
      let sliderContainerWidth = this.$refs.sliderContainer.clientWidth
      let elementsToShow = 3
      let cardWidth = sliderContainerWidth / elementsToShow
     
      if(Math.round(+slider.style.marginLeft.slice(0,-2)) != 0) {
        slider.style.marginLeft = ((+slider.style.marginLeft.slice(0,-2)) +cardWidth) + 'px' 
      }
     
      
    
    }
  },
  mounted() {
    let sliderContainer = document.getElementById('sliderContainer')
    let slider = document.getElementById('slider')
    let cards = slider.getElementsByTagName('li')
    let elementsToShow = 3
    let sliderContainerWidth = this.$refs.sliderContainer.clientWidth
   
    
    let cardWidth = sliderContainerWidth / elementsToShow

    slider.style.width = cards.length * cardWidth + 'px'

    for (let index = 0; index < cards.length; index++) {
      const element = cards[index];
      element.style.width = cardWidth + 'px'
      
    }

   
  }
}
</script>

